import React, { useState } from "react";
import { auth, googleProvider, githubProvider, signInWithPopup } from "../firebaseConfig";

function Login() {
  const [user, setUser] = useState(null);

  const handleLogin = async (provider) => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
      console.log("User Info:", result.user);
    } catch (error) {
      console.error("Login failed:", error.message);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "3rem" }}>
      <h2>Sign in to Access Portfolio Features</h2>
      <button onClick={() => handleLogin(googleProvider)}>Sign in with Google</button>
      <button onClick={() => handleLogin(githubProvider)}>Sign in with GitHub</button>

      {user && (
        <div>
          <h3>Welcome, {user.displayName}</h3>
          <img src={user.photoURL} alt="profile" width={80} style={{ borderRadius: "50%" }} />
          <p>{user.email}</p>
        </div>
      )}
    </div>
  );
}

export default Login;
