import React, { useEffect, useRef } from 'react';
import './MainHero.css';
import Navbar from './Navbar';
import Footer from './Footer';

const skills = [
  { name: 'React.js', percent: 92 },
  { name: 'Node.js', percent: 88 },
  { name: 'MongoDB', percent: 80 },
  { name: 'Adobe Photoshop', percent: 75 },
  { name: 'HTML5 & CSS3', percent: 95 },
  { name: 'Tailwind CSS', percent: 85 },
];

export default function Main() {
  // Intersection Observer for skill bars
  const skillRefs = useRef([]);
  useEffect(() => {
    const observer = new window.IntersectionObserver(
      entries => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            const percent = entry.target.getAttribute('data-percent');
            entry.target.style.setProperty('--skill-bar-width', percent + '%');
            entry.target.classList.add('skill-bar-animate');
          }
        });
      },
      { threshold: 0.4 }
    );
    skillRefs.current.forEach(ref => {
      if (ref) observer.observe(ref);
    });
    return () => observer.disconnect();
  }, []);

  return (
    <>
      <Navbar />
      <section className="main-hero-section d-flex align-items-center justify-content-center min-vh-100 bg-white position-relative overflow-hidden">
        <div className="container-fluid px-4 px-md-5">
          <div className="row align-items-center justify-content-center flex-column-reverse flex-md-row">
            {/* Left Side */}
            <div className="col-md-6 text-center text-md-start mb-5 mb-md-0 animate__animated animate__fadeInLeft animate__delay-1s">
              <div className="main-hero-subheading mb-2">MERN Stack Developer</div>
              <h1 className="main-hero-heading mb-2">
                Hello, I'm <span className="main-hero-name">Rahul Sharma</span>
              </h1>
              <p className="main-hero-desc mb-4">
                I design and build modern, high-performance web applications with a passion for clean code, creative UI, and seamless user experiences. Let's collaborate to bring your ideas to life!
              </p>
              <a href="#contact" className="main-hero-btn">GET IN TOUCH</a>
            </div>
            {/* Right Side */}
            <div className="col-md-6 d-flex justify-content-center align-items-center position-relative animate__animated animate__fadeInRight animate__delay-2s">
              <div className="main-hero-img-bg position-absolute top-50 start-50 translate-middle">
                {/* Animated SVG Dashed Curves */}
                <svg className="main-hero-dashed-svg" width="260" height="260" viewBox="0 0 260 260" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <circle cx="130" cy="130" r="120" stroke="#ff3b3b" strokeWidth="3" strokeDasharray="12 12" fill="none">
                    <animateTransform attributeName="transform" type="rotate" from="0 130 130" to="360 130 130" dur="12s" repeatCount="indefinite" />
                  </circle>
                </svg>
                {/* Floating Accent Elements */}
                <span className="main-hero-float main-hero-dot" />
                <span className="main-hero-float main-hero-triangle" />
                <span className="main-hero-float main-hero-circle" />
                <span className="main-hero-float main-hero-icon">
                  <i className="ri-code-s-slash-line" />
                </span>
              </div>
              <div className="main-hero-img-wrapper position-relative">
                <img src="images/dp7.jpg" alt="Rahul Sharma" className="main-hero-img" />
              </div>
            </div>
          </div>
        </div>
      </section>
      <section className="whyhire-section py-5 px-2 px-md-5 position-relative bg-light whyhire-bg">
        {/* Floating background shapes */}
        <span className="whyhire-float whyhire-triangle" />
        <span className="whyhire-float whyhire-circle" />
        <span className="whyhire-float whyhire-dot" />
        <div className="container-xxl">
          <div className="row g-5 align-items-center justify-content-center flex-column flex-md-row">
            {/* Left: Why Hire Me */}
            <div className="col-md-6 animate__animated animate__fadeInLeft animate__delay-1s">
              <h2 className="whyhire-heading mb-4">Why Hire Me?</h2>
              <p className="whyhire-desc mb-3">
                I bring a unique blend of creativity, technical expertise, and a relentless drive for excellence. My approach is always user-focused, ensuring every project is not only functional but also visually engaging and intuitive.
              </p>
              <p className="whyhire-desc mb-4">
                With a proven track record in delivering high-quality web solutions, I thrive in collaborative environments and adapt quickly to new challenges. Let's work together to turn your vision into reality!
              </p>
              <div className="d-flex gap-3 flex-wrap">
                <a href="#contact" className="whyhire-btn whyhire-btn-dark">HIRE ME</a>
                <a href="/cv.pdf" className="whyhire-btn whyhire-btn-outline" download>DOWNLOAD CV</a>
              </div>
            </div>
            {/* Right: My Skills */}
            <div className="col-md-6 animate__animated animate__fadeInRight animate__delay-2s">
              <h2 className="whyhire-heading mb-4">My Skills</h2>
              <div className="whyhire-skills-list">
                {skills.map((skill, idx) => (
                  <div className="whyhire-skill mb-4" key={skill.name}>
                    <div className="d-flex justify-content-between align-items-center mb-1">
                      <span className="whyhire-skill-name">{skill.name}</span>
                      <span className="whyhire-skill-percent">{skill.percent}%</span>
                    </div>
                    <div className="whyhire-skill-bar-bg">
                      <div
                        className="whyhire-skill-bar"
                        ref={el => (skillRefs.current[idx] = el)}
                        style={{ width: 0 }}
                        data-percent={skill.percent}
                      />
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </section>
      <Footer />
    </>
  );
}
