import React, { useState } from "react";
import { Link } from "react-router-dom";

function ResumePopup({ show, onClose }) {
  if (!show) return null;
  return (
    <div className="resume-popup-overlay">
      <div className="resume-popup-content animate-popup">
        <button className="resume-popup-close" onClick={onClose}>
          &times;
        </button>
        <img
          src="/images/download-anim.gif"
          alt="Download Complete"
          className="resume-popup-gif"
        />
        <h4 className="resume-popup-title">Download Complete!</h4>
        <p className="resume-popup-desc">
          Check your Downloads folder for Resume.
          <br />
          Thank you for your interest!
        </p>
      </div>
    </div>
  );
}

export default function Navbar() {
  const [showPopup, setShowPopup] = useState(false);

  const handleResumeClick = (e) => {
    // Start download
    const link = document.createElement("a");
    link.href = "/images/RESUME_RAHUL.pdf";
    link.download = "RESUME_RAHUL.pdf";
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    // Show popup
    setShowPopup(true);
    setTimeout(() => setShowPopup(false), 2600);
    e.preventDefault();
  };

  return (
    <>
      <nav
        className="navbar navbar-expand-lg"
        style={{
          background:
            "linear-gradient(90deg,rgba(255, 0, 0, 0.67), #d55959 100%)",
          boxShadow: "0 2px 12px rgba(44,83,100,0.15)",
        }}
      >
        <div
          className="container-fluid d-flex flex-column flex-lg-row align-items-center justify-content-between position-relative"
          style={{ minHeight: 70 }}
        >
          {/* Left nav links */}
          <div className="d-none d-lg-flex flex-grow-1 justify-content-start">
            <ul
              className="navbar-nav d-flex flex-row align-items-center"
              style={{ gap: "2.5rem", margin: 0 }}
            >
              <li className="nav-item">
                <Link
                  className="nav-link active"
                  style={{ color: "#232526" }}
                  aria-current="page"
                  to="/"
                >
                  Home
                </Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" style={{ color: "#232526" }} to="/blog">
                  Blog
                </Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" style={{ color: "#232526" }} to="/skills">
                  Skills
                </Link>
              </li>
            </ul>
          </div>
          {/* Center brand */}
          <div
            className="mx-auto text-center"
            style={{
              position: "absolute",
              left: 0,
              right: 0,
              top: 0,
              bottom: 0,
              margin: "auto",
              width: "fit-content",
              height: "fit-content",
              zIndex: 2,
            }}
          >
            <Link
              className="navbar-brand d-flex align-items-center gap-2 justify-content-center"
              to="/"
            >
              <img
                src="/images/dp7.jpg"
                alt="logo"
                width="44"
                height="44"
                style={{
                  border: "2px solid black",
                  borderRadius: "50%",
                  objectFit: "cover",
                }}
                className="d-inline-block"
              />
              <h2
                style={{
                  margin: 0,
                  fontSize: "1.7rem",
                  fontWeight: 700,
                  color: "#232526",
                  letterSpacing: "-1px",
                }}
              >
                Rahul Sharma
              </h2>
            </Link>
          </div>
          {/* Right nav links */}
          <div className="d-none d-lg-flex flex-grow-1 justify-content-end">
            <ul
              className="navbar-nav d-flex flex-row align-items-center"
              style={{ gap: "2.5rem", margin: 0 }}
            >
              <li className="nav-item">
                <Link className="nav-link" style={{ color: "#232526" }} to="/projects">
                  Projects
                </Link>
              </li>
              <li className="nav-item">
                <a
                  className="nav-link"
                  style={{ color: "#232526" }}
                  href="/images/RESUME_RAHUL.pdf"
                  onClick={handleResumeClick}
                >
                  Resume
                </a>
              </li>
              <li className="nav-item">
                <Link className="nav-link" style={{ color: "#232526" }} to="/contactpage">
                  Contact
                </Link>
              </li>
            </ul>
          </div>
          {/* Mobile nav toggle and collapse */}
          <button
            className="navbar-toggler d-lg-none ms-auto"
            type="button"
            data-bs-toggle="collapse"
            data-bs-target="#navbarSupportedContent"
            aria-controls="navbarSupportedContent"
            aria-expanded="false"
            aria-label="Toggle navigation"
          >
            <span className="navbar-toggler-icon" />
          </button>
          <div className="collapse navbar-collapse d-lg-none" id="navbarSupportedContent">
            <ul
              className="navbar-nav d-flex flex-column align-items-center w-100"
              style={{ margin: 0, gap: "1.5rem" }}
            >
              <li className="nav-item">
                <Link className="nav-link active" style={{ color: "#232526" }} aria-current="page" to="/">
                  Home
                </Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" style={{ color: "#232526" }} to="/blog">
                  Blog
                </Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" style={{ color: "#232526" }} to="/skills">
                  Skills
                </Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" style={{ color: "#232526" }} to="/projects">
                  Projects
                </Link>
              </li>
              <li className="nav-item">
                <a className="nav-link" style={{ color: "#232526" }} href="/images/RESUME_RAHUL.pdf" onClick={handleResumeClick}>
                  Resume
                </a>
              </li>
              <li className="nav-item">
                <Link className="nav-link" style={{ color: "#232526" }} to="/contactpage">
                  Contact
                </Link>
              </li>
            </ul>
          </div>
        </div>
      </nav>
      <ResumePopup show={showPopup} onClose={() => setShowPopup(false)} />
    </>
  );
}

// Add to your CSS (Navbar.css or global):
/*
.resume-popup-overlay {
  position: fixed;
  top: 0; left: 0; right: 0; bottom: 0;
  background: rgba(30,30,30,0.25);
  z-index: 9999;
  display: flex;
  align-items: center;
  justify-content: center;
  animation: fadeInBg 0.3s;
}
@keyframes fadeInBg {
  from { opacity: 0; }
  to { opacity: 1; }
}
.resume-popup-content {
  background: rgba(255,255,255,0.98);
  border-radius: 1.5rem;
  box-shadow: 0 8px 40px 0 rgba(255,59,59,0.18);
  padding: 2.2rem 2.5rem 1.7rem 2.5rem;
  text-align: center;
  min-width: 320px;
  max-width: 90vw;
  position: relative;
  animation: popupScaleIn 0.5s cubic-bezier(.68,-0.55,.27,1.55);
}
@keyframes popupScaleIn {
  0% { transform: scale(0.7) translateY(40px); opacity: 0; }
  80% { transform: scale(1.05) translateY(-8px); opacity: 1; }
  100% { transform: scale(1) translateY(0); opacity: 1; }
}
.resume-popup-gif {
  width: 90px;
  height: 90px;
  margin-bottom: 1.1rem;
}
.resume-popup-title {
  color: #ff3b3b;
  font-size: 1.3rem;
  font-weight: 700;
  margin-bottom: 0.5rem;
}
.resume-popup-desc {
  color: #232526;
  font-size: 1.05rem;
  margin-bottom: 0.2rem;
