import React, { useState } from "react";
import { auth, googleProvider, githubProvider, signInWithPopup, signInWithEmailAndPassword } from "../firebaseConfig";

function Login() {
  const [user, setUser] = useState(null);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleLogin = async (provider) => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
      setError("");
    } catch (error) {
      setError(error.message);
    }
  };

  const handleEmailLogin = async (e) => {
    e.preventDefault();
    setError("");
    try {
      const result = await signInWithEmailAndPassword(auth, email, password);
      setUser(result.user);
    } catch (error) {
      setError(error.message);
    }
  };

  return (
    <div style={{ textAlign: "center", marginTop: "3rem" }}>
      <h2>Sign in to Access Portfolio Features</h2>
      <form
        onSubmit={handleEmailLogin}
        style={{ margin: '2rem auto', maxWidth: 340, background: '#fff', borderRadius: 16, boxShadow: '0 4px 24px 0 rgba(255,59,59,0.08)', padding: '2rem 2rem 1.5rem 2rem', display: 'flex', flexDirection: 'column', gap: '1.1rem', alignItems: 'center' }}
      >
        <input
          type="email"
          placeholder="Email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          style={{ width: '100%', padding: '0.7rem', borderRadius: 8, border: '1px solid #eee', fontSize: '1rem' }}
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={e => setPassword(e.target.value)}
          style={{ width: '100%', padding: '0.7rem', borderRadius: 8, border: '1px solid #eee', fontSize: '1rem' }}
          required
        />
        <button type="submit" style={{ width: '100%', background: 'linear-gradient(90deg,#ff3b3b 0%,#ffb347 100%)', color: '#fff', fontWeight: 700, border: 'none', borderRadius: 8, padding: '0.7rem', fontSize: '1.08rem', cursor: 'pointer' }}>Login</button>
      </form>
      {error && <div style={{ color: '#ff3b3b', marginBottom: '1rem', fontWeight: 600 }}>{error}</div>}
      <div style={{ margin: '1.5rem 0 1rem 0', fontWeight: 600, color: '#888' }}>or</div>
      <button onClick={() => handleLogin(googleProvider)} style={{ margin: '0 0.5rem', background: '#fff', color: '#232526', border: '1px solid #eee', borderRadius: 8, padding: '0.7rem 1.5rem', fontWeight: 600, fontSize: '1rem', boxShadow: '0 2px 10px 0 rgba(255,59,59,0.04)', cursor: 'pointer' }}>Sign in with Google</button>
      <button onClick={() => handleLogin(githubProvider)} style={{ margin: '0 0.5rem', background: '#232526', color: '#fff', border: 'none', borderRadius: 8, padding: '0.7rem 1.5rem', fontWeight: 600, fontSize: '1rem', boxShadow: '0 2px 10px 0 rgba(255,59,59,0.04)', cursor: 'pointer' }}>Sign in with GitHub</button>
      {user && (
        <div style={{ marginTop: '2rem' }}>
          <h3>Welcome, {user.displayName || user.email}</h3>
          {user.photoURL && <img src={user.photoURL} alt="profile" width={80} style={{ borderRadius: "50%" }} />}
          <p>{user.email}</p>
        </div>
      )}
    </div>
  );
}

export default Login;
