import React, { useState } from "react";
import "./ProjectsSection.css";

const devProjects = [
  {
    id: 1,
    title: "E-commerce Platform",
    desc: "A scalable online store with payment integration and admin dashboard.",
    tech: ["React", "Node.js", "MongoDB"],
  },
  {
    id: 2,
    title: "Portfolio Website",
    desc: "A modern, animated portfolio SPA for developers and designers.",
    tech: ["React", "Framer Motion", "Bootstrap"],
  },
  {
    id: 3,
    title: "Task Manager App",
    desc: "A productivity app with real-time sync and notifications.",
    tech: ["React", "Redux", "Firebase"],
  },
];

const videoProjects = [
  {
    id: 1,
    title: "Travel Vlog Edit",
    desc: "Dynamic montage of travel clips with transitions and color grading.",
    tech: ["Premiere Pro", "After Effects"],
  },
  {
    id: 2,
    title: "Product Promo Video",
    desc: "Short promotional video for a tech product launch.",
    tech: ["Final Cut Pro", "Photoshop"],
  },
  {
    id: 3,
    title: "Event Highlights",
    desc: "Highlight reel for a corporate event with motion graphics.",
    tech: ["DaVinci Resolve", "After Effects"],
  },
];

export default function ProjectsSection() {
  const [active, setActive] = useState("dev");
  const projects = active === "dev" ? devProjects : videoProjects;

  return (
    <section className="projects-section">
      <div className="projects-header">
        <h2 className="projects-title">My Projects</h2>
        <div className="projects-underline" />
        <p className="projects-subtitle">
          A selection of my recent work in both development and video editing.
        </p>
        <div className="projects-filters">
          <button
            className={`projects-filter-btn${active === "dev" ? " active" : ""}`}
            onClick={() => setActive("dev")}
          >
            <span className="projects-filter-icon">{'</>'}</span> Development Projects
          </button>
          <button
            className={`projects-filter-btn${active === "video" ? " active" : ""}`}
            onClick={() => setActive("video")}
          >
            <span className="projects-filter-icon">ðŸŽ¥</span> Video Projects
          </button>
        </div>
      </div>
      <div className="projects-cards">
        {projects.map((proj) => (
          <div className="project-card" key={proj.id}>
            <div className="project-img-placeholder" />
            <div className="project-card-body">
              <h3 className="project-title">{proj.title}</h3>
              <p className="project-desc">{proj.desc}</p>
              <div className="project-badges">
                {proj.tech.map((t) => (
                  <span className="project-badge" key={t}>{t}</span>
                ))}
              </div>
            </div>
          </div>
        ))}
      </div>
      <div className="projects-bottom-btn-wrap">
        <button className="projects-bottom-btn">
          View All Development Projects
        </button>
      </div>
    </section>
  );
}
